mapper.select:
SELECT AAA.* FROM( SELECT AA.* FROM( SELECT ROW_NUMBER() OVER (ORDER BY ID DESC) RNUM, ID, TITLE, CONTENTS, WRITER, REGDATE, HIT FROM NOTICE
WHERE ${param2} LIKE '%${param3}%'
) AA ) AAA WHERE AAA.RNUM BETWEEN #{param1.startRowNum} AND #{param1.endRowNum}

mapper.update:
UPDATE NOTICE
SET TITLE=#{TITLE}, CONTENTS=#{CONTENTS}, REGDATE=SYSDATE
WHERE ID=#{ID}
